grid = [list(l) for l in input.splitlines()]
start = [grid[0].index("|"), 0]

def get(coords):
	try:
		return grid[coords[1]][coords[0]]
	except:
		return None

def add(coords, dir):
	cx,cy = coords
	dx,dy = dir
	return cx+dx, cy+dy

def flip(coords):
	x,y = coords
	return [-x, -y]

seen = []

def step(cur, dir):
	char = get(cur)
	if char in ["|", "-"]:
		return add(cur, dir), dir
	if char == "+":
		for d in [[0,1], [1,0], [0,-1], [-1,0]]:
			nxt = add(cur, d)
			if d != flip(dir) and get(nxt) not in [" ", None]:
				return nxt, d
	else:
		seen.append(char)
		return add(cur, dir), dir

cur = start
char = get(start)
dir = [0, 1]
n = 0
while char not in [" ", None]:
	cur, dir = step(cur, dir)
	char = get(cur)
	n+=1

print("".join(seen))
print(n)


		
	
